import './ProductScreen.css';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getProductDetails } from '../redux/actions/productActions';
import { addToCart } from '../redux/actions/cartActions';
const ProductScreen = () => {
    const params = useParams();
    const [qty, setQty] = useState(1);
    const dispatch = useDispatch();
    const getProductDetail = useSelector(state => state.getProductDetail);
    const { product, loading, error } = getProductDetails;

    useEffect(() => {
        if (product && params.id != product._id) {
            dispatch(getProductDetails(params.id));
        }
    }, [dispatch, product, params]);

    return (

        <div className="productscreen">
            {loading && (<h2>Loading...</h2>)}
            {!loading && error && <h2>{error}</h2>}
            <div className="productscreen__left">
                <div className="left__image">
                    <img src={product.imageUrl} alt="" />
                </div>
                <div className="left__info">
                    <p className="left__name">Product 1</p>
                    <p className="left__price">$400</p>
                    <p className="left__description">lorem</p>
                </div>
            </div>
            <div className="productscreen__right">
                <div className="right__info">
                    <p>
                        Price: <span>$400</span>
                    </p>
                    <p>
                        Status: <span>In stock</span>
                    </p>
                    <p>
                        Qty
                        <select>
                            <option value="1">1</option>
                            <option value="1">2</option>
                            <option value="1">3</option>
                        </select>
                    </p>
                    <p>
                        <button type="button">Add To Cart</button>
                    </p>
                </div>
            </div>
        </div>
    )
}

export default ProductScreen;
