import "./CartItem.css";

const { Link } = require("react-router-dom")

const CartItem = ({ productId, imageUrl, name, qty, countInStock, price }) => {
    return (
        <div className="cartitem">
            <div className="cartitem__image">
                <img src={imageUrl} alt="" />
            </div>
            <Link to={`/product/${productId}`} className="cartitem__name" >
                <p>{name}</p>
            </Link>
            <p className="cartitem__price">${qty * price}</p>
            <select value={qty} className="cartitem__select">
                {[...Array(countInStock).keys()].map(x => <option value={x + 1}>{x + 1}</option>)}
            </select>
            <button className="cartitem__deleteBtn">
                <i className="fas fa-trash"></i>
            </button>
        </div>
    )
}

export default CartItem;
